mapper.update:
UPDATE "MEMBER_MENU"
SET FAVORITE = 
'N'
'Y'
WHERE MEMBER_NO = #{loginMemberNo}
AND BOTTOM_MENU_CODE = #{bottomMenuName}

mapper.select:
SELECT FAVORITE, BOTTOM_MENU_NAME
FROM MEMBER_MENU
JOIN BOTTOM_MENU USING(BOTTOM_MENU_CODE)
WHERE MEMBER_NO = #{memberNo}
AND BOTTOM_MENU_CODE = #{bottomMenuCode}
